export class DashboardEvent {
    constructor() {
        this.handlers = [];
    }
    add(handler) {
        this.handlers.push(handler);
        return {
            dispose: () => this.remove(handler)
        };
    }
    remove(handler) {
        this.handlers = this.handlers.filter(h => h !== handler);
    }
    fire(args) {
        this.handlers.forEach(h => h(args));
    }
}
