export class DisposableObject {
    constructor() {
        this._disposables = [];
        this.disposed = false;
    }
    toDispose(...disposables) {
        this._disposables.push(...disposables);
    }
    dispose() {
        this._disposables.forEach(disposable => disposable.dispose());
        this._disposables = [];
        this.disposed = true;
    }
}
