import { ModelSerializer } from '@devexpress/analytics-core/analytics-utils';
import * as ko from 'knockout';
import { DashboardItemFormatRule } from './dashboard-item-format-rule';
export class CardItemFormatRuleBase extends DashboardItemFormatRule {
    constructor(modelJson = {}, serializer = new ModelSerializer()) {
        super(modelJson, serializer);
        this.addDisposable(this.layoutItemApplyTo = ko.pureComputed({
            read: () => {
                return this.cardLayoutElement.elementTypeApplyTo();
            },
            write: (newLayoutElement) => {
                if (!(newLayoutElement === 'Dimension' || newLayoutElement === 'Text'))
                    this.cardLayoutElement.elementTypeApplyTo(newLayoutElement);
            }
        }));
    }
}
