import { ModelSerializer } from '@devexpress/analytics-core/analytics-utils';
import { DashboardItemFormatRule } from './dashboard-item-format-rule';
import { conditionTypes } from './metadata/_dashboard-item-format-rule';
import { ColorStyleSettings } from './style-settings/color-style-settings';
export class ChartItemFormatRuleBase extends DashboardItemFormatRule {
    constructor(modelJson = {}, serializer = new ModelSerializer()) {
        super(modelJson, serializer);
        if (!this.condition()) {
            conditionTypes
                .map(type => type.propertyName)
                .forEach(propertyName => this[propertyName] && this[propertyName].styleSettings && this[propertyName].styleSettings(new ColorStyleSettings({})));
        }
    }
}
