export class LimitDataState {
    constructor() {
        this._visible = false;
        this._enabled = true;
    }
    get enabled() {
        return this._enabled;
    }
    setReduced() {
        this._visible = true;
        this._enabled = true;
    }
    getViewModel() {
        return {
            isReduceMode: this._visible,
            isReduced: this._enabled,
        };
    }
    toggle() {
        this._enabled = !this._enabled;
    }
    reset() {
        this._visible = false;
        this._enabled = true;
    }
}
