import { ModelSerializer, deserializeArray } from '@devexpress/analytics-core/analytics-utils';
import { SerializableModel } from '../../serializable-model';
import { ChartSeriesCreator } from './chart-series-creator';
import { chartPaneSerializationsInfo } from './metadata/_chart-pane';
export class ChartPane extends SerializableModel {
    constructor(dataItemProvider, dashboardItemJSON = {}, serializer = new ModelSerializer()) {
        super(dashboardItemJSON, serializer);
        this.createSeriesByViewType = ChartSeriesCreator.getSeriesCreator(dataItemProvider);
        this.series = deserializeArray(dashboardItemJSON.Series, (item) => ChartSeriesCreator.createSeries(dataItemProvider, item, serializer));
    }
    dispose() {
        super.dispose();
        this.disposeObservableArray(this.series);
    }
    getInfo() {
        return chartPaneSerializationsInfo;
    }
}
